import React from "react";

export const ContactForm = ({
  values,
  handleChange,
  onSubmit,
  isDuplicate
}) => {
  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent default form submission
    onSubmit(); // Invoke the onSubmit function passed as prop
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        {isDuplicate && <p className="error">This name already exists!</p>}
        <label htmlFor="name">Name</label>
        <input 
          type="text"
          id="name"
          name="name"
          value={values.name}
          onChange={handleChange}
          required
        />
        <label htmlFor="phone">Phone number</label>
        <input 
          type="tel"
          id="phone"
          name="phone"
          value={values.phone}
          onChange={handleChange}
          pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
          placeholder="Your phone number"
          required
        />
        <label htmlFor="email">Email</label>
        <input 
          type="email"
          id="email"
          name="email"
          value={values.email}
          onChange={handleChange}
          required
        />
        <input type="submit" value="Add Contact" /> {/* Moved onSubmit to form tag */}
      </form>
    </>
  );
};
