import React, { useState, useEffect } from "react";
import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";

export const ContactsPage = ({ contacts }) => {
  const [contactInfo, setContactInfo] = useState({
    name: "",
    phone: "",
    email: "",
  });

  const [isDuplicate, setIsDuplicate] = useState(false);

  const addContact = (newContact) => {
    // Logic to add the new contact to the list of contacts
  };

  const handleSubmit = (e) => {
    if(e) {
      e.preventDefault();
    }

    // Check for duplicate name before adding
    if (!isDuplicate) {
      addContact(contactInfo); // Pass the entire contactInfo object
      setContactInfo({ name: "", phone: "", email: "" }); // Clear form
    }
  };

  // Check for duplicate name on every change
  useEffect(() => {
    if (contacts) {
      const isExistingContact = contacts.some(
        (contact) => contact.name === contactInfo.name
      );
      setIsDuplicate(isExistingContact);
    }
  }, [contacts, contactInfo.name]);

  return (
    <div>
      <section>
        <h2>Add Contact</h2>
        <ContactForm
          values={contactInfo}
          handleChange={(event) =>
            setContactInfo({
              ...contactInfo,
              [event.target.name]: event.target.value,
            })
          }
          onSubmit={handleSubmit}
          isDuplicate={isDuplicate}
          addContact={addContact} // Pass the addContact function as a prop
        />
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
        <TileList items={contacts} />
      </section>
    </div>
  );
};

export default ContactsPage;
